// ensure every test case has an assertion
// (particularly useful for async tests)
beforeEach(function () {
  expect.hasAssertions();
});
var CONSOLE_FAIL_TYPES = ['error', 'warn']; // Throw errors when a `console.error` or `console.warn` happens

CONSOLE_FAIL_TYPES.forEach(function (type) {
  // eslint-disable-next-line no-console
  console[type] = function (message) {
    throw new Error("Failing due to console.".concat(type, " while running test!\n\n").concat(message));
  };
});